  computed: {
    total() {
      return this.$store.state.orderItem.items.reduce(
        (currentVal, nextVal) => currentVal + nextVal.price,
        0
      );
    },
    activeClass() {
      return "active";
    }
  },
  methods: {
    addItem(id, price) {
      console.log(price);
      this.$store.commit("addItem", { id, price });
    },
    activeClick: function() {
      this.isActive = !this.isActive;
    }
  }
};
</script>
